import React, { useState, useEffect, useRef } from 'react';

const testimonials = [
  {
    name: 'John Doe',
    role: 'CEO, Tech Solutions',
    image: 'https://via.placeholder.com/150',
    feedback: 'This product has completely transformed the way we do business. Highly recommended!',
  },
  {
    name: 'Jane Smith',
    role: 'Marketing Manager',
    image: 'https://via.placeholder.com/150',
    feedback: 'Amazing service and support. The team is always ready to help and deliver beyond expectations.',
  },
  {
    name: 'Alice Johnson',
    role: 'Product Manager',
    image: 'https://via.placeholder.com/150',
    feedback: 'The best decision we made was choosing this product. It has exceeded all our expectations.',
  },
  {
    name: 'Bob Brown',
    role: 'CTO, Innovate Inc.',
    image: 'https://via.placeholder.com/150',
    feedback: 'Incredible features and excellent customer support. We are very satisfied with the results.',
  },
];

const AutoScrollTestimonial = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const testimonialRef = useRef(null);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % testimonials.length);
    }, 5000); // Change testimonial every 5 seconds

    return () => clearInterval(interval); // Cleanup interval on unmount
  }, []);

  useEffect(() => {
    if (testimonialRef.current) {
      testimonialRef.current.style.opacity = 0; // Fade out
      setTimeout(() => {
        testimonialRef.current.style.opacity = 1; // Fade in
      }, 500); // Match the transition duration
    }
  }, [currentIndex]);

  const { name, role, image, feedback } = testimonials[currentIndex];

  return (
    <div style={styles.container}>
      <div ref={testimonialRef} style={styles.testimonial}>
        <p style={styles.feedback}>{feedback}</p>
        <div style={styles.userInfo}>
          <img src={image} alt={name} style={styles.userImage} />
          <div style={styles.userDetails}>
            <p style={styles.userName}>{name}</p>
            <p style={styles.userRole}>{role}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

// Styles for the AutoScrollTestimonial Component
const styles = {
  container: {
    backgroundColor: '#f9f9f9',
    borderRadius: '10px',
    padding: '20px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    maxWidth: '600px',
    margin: '20px auto',
    textAlign: 'center',
    overflow: 'hidden',
  },
  testimonial: {
    transition: 'opacity 0.5s ease-in-out',
  },
  feedback: {
    fontSize: '16px',
    lineHeight: '1.6',
    color: '#333',
    marginBottom: '20px',
    fontStyle: 'italic',
  },
  userInfo: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  userImage: {
    width: '60px',
    height: '60px',
    borderRadius: '50%',
    marginRight: '15px',
    objectFit: 'cover',
  },
  userDetails: {
    textAlign: 'left',
  },
  userName: {
    fontSize: '18px',
    fontWeight: 'bold',
    margin: '0',
    color: '#043873',
  },
  userRole: {
    fontSize: '14px',
    color: '#666',
    margin: '0',
  },
};

export default AutoScrollTestimonial;
