import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Pagination, Navigation } from 'swiper/modules';

import 'swiper/css';
import 'swiper/css/pagination';
import 'swiper/css/navigation';

const WhatCompanyOffers = () => {
  const useCases = [
    {
      title: 'Conventional AI',
      description:
        'Data validation is the process of checking the accuracy, consistency, and completeness of data before it is used. It is a type of data cleansing.',
      image: 'https://placehold.co/400x400',
    },
    {
      title: 'Conventional AI',
      description:
        'Data translation can be defined as the process of converting volumes of data from one syntax to another and performing value lookups or substitutions from the data during the process.',
      image: 'https://placehold.co/400x400',
    },


  ];

  return (
    <div>
      <div>
        <div style={styles.heading}>OUR USE CASES EXPERTISE</div>
      </div>
      <div style={{ width: '100%', maxWidth: '1200px', margin: '0 auto', padding: '2rem 0' }}>
        <Swiper
          slidesPerView={1}
          spaceBetween={20}
          pagination={{ clickable: true }}
          navigation={true}
          breakpoints={{
            640: { slidesPerView: 1 },
            768: { slidesPerView: 2 },
            1024: { slidesPerView: 3 },
          }}
          modules={[Pagination, Navigation]}
        >
          {useCases.map((item, index) => (
            <SwiperSlide key={index}>
              <div style={styles.achievementItem}>
                <img src={item.image} alt={item.title} style={styles.achievementImage} />
                <h2 style={styles.achievementNumber}>{item.title}</h2>
                <p style={styles.achievementText}>{item.description}</p>
              </div>
            </SwiperSlide>
          ))}
        </Swiper>
      </div>
    </div>
  );
};

const styles = {
  heading: {
    backgroundColor: '#007BFF',
    color: 'white',
    padding: '0.5rem 1rem',
    borderRadius: '25px',
    textAlign: 'center',
    display: 'inline-block',
    margin: '1rem auto',
    fontSize: '1.2rem',
    fontWeight: 'bold',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
  },
  achievementItem: {
    textAlign: 'center',
    backgroundColor: 'white',
    padding: '1.5rem',
    borderRadius: '10px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    width: '100%',
    height: '400px',
  },
  achievementNumber: {
    color: '#000000',
    textAlign: 'left',
    fontSize: '1.2rem',
  },
  achievementText: {
    fontSize: '1rem',
    color: '#333',
    margin: '0',
  },
  achievementImage: {
    width: '150px',
    height: '130px',
  },
};

export default WhatCompanyOffers;
