import React from 'react';

const FavoriteAppsSection = () => {
    return (
        <div style={styles.container}>

            <div style={styles.containerCircle}>
                <div style={styles.circle}></div>
                <div style={{ ...styles.circle, left: '80px' }}></div> {/* Adjust the overlap here */}
                <div style={{ ...styles.circle, bottom: '80px', left: '40px' }}></div>
            </div>
            <div>
                <h2 style={styles.heading}>Work with Your Favorite Apps Using Whitepace</h2>
            <h1 style={styles.mainHeading}>Favorite Apps Using Whitepace</h1>
            <p style={styles.description}>
                Whitepace teams up with your favorite software. Integrate with over 1000+ apps with Zapier to
                have all the tools you need for your project success.
            </p>
            <button style={styles.button}>Read more</button>
            </div>
        </div>
    );
};

// Styles for the component
const styles = {
    containerCircle: {
        position: 'relative',
        width: '150px',
        height: '150px',
        left: '100px',
        top: '50px',
      },
      circle: {
        width: '170px',
        height: '170px',
        borderRadius: '50%',
        border: '2px dashed #4F9CF9',
        position: 'absolute',  // This makes the circles overlap
      },
    container: {
        display: 'flex',
        textAlign: 'center',
        alignItems: 'center',
        justifyContent: 'space-around',
        padding: '40px 20px',
        backgroundColor: '#f9f9f9',
        borderRadius: '10px',
        width: '95%', // Set width to 98%
        height: '400px',
        margin: '0 auto', // Center the container
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    },
    heading: {
        fontSize: '24px',
        color: '#333',
        marginBottom: '10px',
    },
    mainHeading: {
        fontSize: '36px',
        color: '#043873',
        marginBottom: '20px',
    },
    description: {
        fontSize: '18px',
        color: '#666',
        lineHeight: '1.6',
        marginBottom: '30px',
    },
    button: {
        padding: '10px 20px',
        backgroundColor: '#007BFF',
        color: '#fff',
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer',
        fontSize: '16px',
    },
};

export default FavoriteAppsSection;
