import { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";

const testimonials = [
  { id: 1, name: "John Doe", text: "This service is amazing! Highly recommend it." },
  { id: 2, name: "Jane Smith", text: "Great experience, will use again." },
  { id: 3, name: "Mike Johnson", text: "Fantastic support and quality service." },
  { id: 4, name: "Emily Davis", text: "Very satisfied with my purchase." },
  { id: 5, name: "Chris Brown", text: "Affordable and efficient!" }
];

const TestimonialSlider = () => {
  const [index, setIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setIndex((prevIndex) => (prevIndex + 1) % testimonials.length);
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  const getVisibleTestimonials = () => {
    return [
      testimonials[index],
      testimonials[(index + 1) % testimonials.length],
      testimonials[(index + 2) % testimonials.length]
    ];
  };

  return (
    <div className="w-80 h-80 p-4 bg-gray-100 rounded-xl shadow-lg overflow-hidden">
      <AnimatePresence>
        {getVisibleTestimonials().map((testimonial) => (
          <motion.div
            key={testimonial.id}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            transition={{ duration: 0.5 }}
            className="p-3 bg-white rounded-lg shadow mb-2"
          >
            <p className="text-gray-700">"{testimonial.text}"</p>
            <p className="text-gray-900 font-semibold mt-2">- {testimonial.name}</p>
          </motion.div>
        ))}
      </AnimatePresence>
    </div>
  );
};

export default TestimonialSlider;
