import React from 'react';

const Testimonial = ({ name, role, image, feedback }) => {
  return (
    <div style={styles.testimonialContainer}>
      <div style={styles.testimonialContent}>
        <p style={styles.feedback}>{feedback}</p>
        <div style={styles.userInfo}>
          <img src={image} alt={name} style={styles.userImage} />
          <div style={styles.userDetails}>
            <p style={styles.userName}>{name}</p>
            <p style={styles.userRole}>{role}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

// Styles for the Testimonial Component
const styles = {
  testimonialContainer: {
    backgroundColor: '#f9f9f9',
    borderRadius: '10px',
    padding: '20px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    maxWidth: '400px',
    margin: '20px auto',
    textAlign: 'center',
  },
  testimonialContent: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  feedback: {
    fontSize: '16px',
    lineHeight: '1.6',
    color: '#333',
    marginBottom: '20px',
    fontStyle: 'italic',
  },
  userInfo: {
    display: 'flex',
    alignItems: 'center',
  },
  userImage: {
    width: '60px',
    height: '60px',
    borderRadius: '50%',
    marginRight: '15px',
    objectFit: 'cover',
  },
  userDetails: {
    textAlign: 'left',
  },
  userName: {
    fontSize: '18px',
    fontWeight: 'bold',
    margin: '0',
    color: '#043873',
  },
  userRole: {
    fontSize: '14px',
    color: '#666',
    margin: '0',
  },
};

export default Testimonial;
