import React from 'react';

const FavoriteAppsSection = () => {
  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Work with Your</h2>
      <h1 style={styles.mainHeading}>Favorite Apps Using Whitepace</h1>
      <p style={styles.description}>
        Whitepace teams up with your favorite software. Integrate with over 1000+ apps with Zapier to
        have all the tools you need for your project success.
      </p>
      <button style={styles.button}>Read more</button>
    </div>
  );
};

// Styles for the component
const styles = {
  container: {
    textAlign: 'center',
    padding: '40px 20px',
    backgroundColor: '#043873',
    borderRadius: '10px',
    width: '95%', // Set width to 98%
    height: '400px',
    margin: '0 auto', // Center the container
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
  },
  heading: {
    fontSize: '24px',
    color: '#333',
    marginBottom: '10px',
  },
  mainHeading: {
    fontSize: '36px',
    color: '#043873',
    marginBottom: '20px',
  },
  description: {
    fontSize: '18px',
    color: '#666',
    lineHeight: '1.6',
    marginBottom: '30px',
  },
  button: {
    padding: '10px 20px',
    backgroundColor: '#007BFF',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
  },
};

export default FavoriteAppsSection;
